const initialState = {
  currentNews: [
      {
    source: {
    "id": "cnn",
    "name": "CNN"
  },
    author: '',
    title: "",
    description: "",
    url: "",
    urlToImage: "",
    publishedAt: "",
    content: ""}
    ],
  loading: false
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_NEWS":
      return { ...state, loading: true };

    case "NEWS_RECEIVED":
      return { ...state, currentNews: action.payload.articles, loading: false };
    default:
      return state;
  }
};

export default reducer;
