import React, { Component } from "react";
import NewsList from "../news-list";
import { getNews } from "../../actions";
import Pagination from "../pagination";
import { connect } from "react-redux";

class App extends Component {
    state = {
        newsPerPage: 5,
        totalNews:0
    };
  componentDidMount() {
    this.props.getNews();
    this.setState({
        totalNews: this.props.length
    })
  }
  paginate = (event, number) =>{
      event.preventDefault();
  };
  render() {
    return (
      <div className="container">
          <nav className="nav fixed-top navbar-sticky-top expand-lg align-text-center">
              <h3 className="c-grey">News</h3>
          </nav>
        <NewsList news={this.props.currentNews} />
        <Pagination paginate={this.paginate} />
      </div>
    );
  }
}
const mapStateToProps = ({ lenght, currentNews }) => {
  return {
    lenght,
    currentNews
  };
};
const mapDispatchToProps = {
  getNews
};
export default connect(mapStateToProps,mapDispatchToProps)(App);
