import React from 'react';
import './news-item.css'
const NewsItem = ({author, title, description, url, urlToImage, publishedAt}) => {
  return (
      <div className="d-block">
        <div className="title d-flex"><h4>{title}</h4> <p className="date">{publishedAt.split('').splice(0,10).join('')}</p></div>
        <div className="img"><img width="550" src={urlToImage} alt="Image of a news" /></div>
        <div className="description">{description}</div>
        <div className="w-100"><p className="author float-right">{author ? author:''}</p></div>
        <div className="button-panel">
          <a href={url} className="btn btn-primary">Read more...</a>
        </div>
      </div>
  );
};
export default NewsItem;
