import React, { Component } from "react";
import NewsList from "../news-list";
import { getNews } from "../../actions";
import Pagination from "../pagination";
import MainNews from '../main-news';
import icon from './menu.svg';
import './app.css'
import { connect } from "react-redux";

class App extends Component {
    state = {
        newsPerPage: 5,
        totalNews:0
    };
  componentWillMount() {
    this.props.getNews();
  }
  render() {
    return (
      <div className="container-fluid">
          <nav className="nav fixed-top bg-primary navbar-expand-lg">
              <button className="navbar-toggler bg-light " type="button" data-toggle="collapse" data-target="#navbarNav"
                      aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                  <span className="navbar-toggler-icon"><img src={icon} width={25} alt="Menu"></img></span>
              </button>
              <div className="collapse navbar-collapse" id="navbarNav">
                  <ul className="navbar-nav mr-auto">
                      <li className="nav-item m-1">
                          <a className="nav-link navbar-brand" href="#about-me">About me</a>
                      </li>
                      <li className="nav-item m-1">
                          <a className="nav-link" href="">What can i do</a>
                      </li>
                      <li className="nav-item m-1">
                          <a className="nav-link" href="">My works</a>
                      </li>
                      <li className="nav-item m-1">
                          <a className="nav-link" href="">Contacts</a>
                      </li>
                  </ul>
              </div>
          </nav>
          <div className="row">
              <div className="main-news col-lg-8 mt-5">
                  {
                      this.props.loading ? <br/>:
                          <MainNews news={this.props.currentNews.slice(0,1)} />
                  }
              </div>

              <div className="main-content col-lg-8">
                  {
                      this.props.loading ? <h2>Loading</h2>:
                          <NewsList news={this.props.currentNews.slice(1)} />
                  }
              </div>
          </div>

      </div>
    );
  }
}
const mapStateToProps = ({ currentNews, loading }) => {
  return {
    currentNews,
      loading
  };
};
const mapDispatchToProps = {
  getNews
};
export default connect(mapStateToProps,mapDispatchToProps)(App);
